import React, { useState } from 'react';
import { Layout, Menu, Table, Switch, Button, Modal, Form, Input, Select, message } from 'antd';
import { HomeOutlined, MailOutlined, OrderedListOutlined, ShopOutlined, SettingOutlined, UserOutlined } from '@ant-design/icons';
import { useMenu } from './MenuContext';
import './AdminPage.css';

const { Header, Sider, Content } = Layout;
const { SubMenu } = Menu;
const { Option } = Select;

const AdminPage = () => {
    const { menuData, setMenuData } = useMenu();
    const [editingItem, setEditingItem] = useState(null);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [form] = Form.useForm();

    const showModal = (item) => {
        setEditingItem(item);
        setIsModalVisible(true);
        if (item) {
            form.setFieldsValue(item);
        } else {
            form.resetFields();
            form.setFieldsValue({ visible: true }); // 确保新建时的初始状态
        }
    };

    const handleOk = () => {
        form.validateFields().then(values => {
            if (editingItem) {
                setMenuData(menuData.map(item => (item.key === editingItem.key ? { ...values, key: editingItem.key } : item)));
                message.success('修改成功');
            } else {
                setMenuData([...menuData, { ...values, key: `${menuData.length + 1}` }]);
                message.success('添加成功');
            }
            setIsModalVisible(false);
            form.resetFields();
        });
    };

    const handleCancel = () => {
        setIsModalVisible(false);
        form.resetFields();
    };

    const handleDelete = (key) => {
        setMenuData(menuData.filter(item => item.key !== key));
        message.success('删除成功');
    };

    const columns = [
        { title: '编号', dataIndex: 'key', key: 'key' },
        { title: '菜单名称', dataIndex: 'name', key: 'name' },
        { title: '菜单级数', dataIndex: 'level', key: 'level' },
        { title: '前端名称', dataIndex: 'frontendName', key: 'frontendName' },
        { title: '前端图标', dataIndex: 'icon', key: 'icon', render: icon => <span className={`anticon anticon-${icon}`}></span> },
        {
            title: '是否显示', dataIndex: 'visible', key: 'visible', render: (text, record) => (
                <Switch checked={record.visible} onChange={() => {
                    setMenuData(menuData.map(item => item.key === record.key ? { ...item, visible: !item.visible } : item));
                }} />
            )
        },
        { title: '排序', dataIndex: 'order', key: 'order' },
        {
            title: '设置', dataIndex: 'settings', key: 'settings', render: (text, record) => (
                <Button type="link" onClick={() => message.info('查看下级功能未实现')}>查看下级</Button>
            )
        },
        {
            title: '操作', key: 'action', render: (text, record) => (
                <span>
                    <Button type="link" onClick={() => showModal(record)}>编辑</Button>
                    <Button type="link" onClick={() => handleDelete(record.key)}>删除</Button>
                </span>
            )
        }
    ];

    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider>
                <div className="logo">管理系统</div>
                <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                    <Menu.Item key="1" icon={<HomeOutlined />}>首页</Menu.Item>
                    {menuData.map(item => (
                        item.visible && (
                            <SubMenu key={item.key} icon={React.createElement({
                                'shop': ShopOutlined,
                                'ordered-list': OrderedListOutlined,
                                'mail': MailOutlined,
                                'setting': SettingOutlined,
                            }[item.icon])} title={item.name}>
                                <Menu.Item key={`${item.key}-list`}>{item.name}列表</Menu.Item>
                            </SubMenu>
                        )
                    ))}
                </Menu>
            </Sider>
            <Layout>
                <Header className="site-layout-background" style={{ padding: 0 }} />
                <Content style={{ margin: '0 16px' }}>
                    <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                        <Button type="primary" onClick={() => showModal(null)}>添加</Button>
                        <Table columns={columns} dataSource={menuData} style={{ marginTop: 16 }} />
                    </div>
                </Content>
            </Layout>
            <Modal title={editingItem ? '编辑菜单' : '添加菜单'} visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
                <Form form={form} layout="vertical">
                    <Form.Item name="name" label="菜单名称" rules={[{ required: true, message: '请输入菜单名称' }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item name="level" label="菜单级数" rules={[{ required: true, message: '请输入菜单级数' }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item name="frontendName" label="前端名称" rules={[{ required: true, message: '请输入前端名称' }]}>
                        <Input />
                    </Form.Item>
                    <Form.Item name="icon" label="前端图标" rules={[{ required: true, message: '请选择前端图标' }]}>
                        <Select>
                            <Option value="shop">shop</Option>
                            <Option value="ordered-list">ordered-list</Option>
                            <Option value="mail">mail</Option>
                            <Option value="setting">setting</Option>
                        </Select>
                    </Form.Item>
                    <Form.Item name="visible" label="是否显示" valuePropName="checked">
                        <Switch />
                    </Form.Item>
                    <Form.Item name="order" label="排序" rules={[{ required: true, message: '请输入排序' }]}>
                        <Input type="number" />
                    </Form.Item>
                </Form>
            </Modal>
        </Layout>
    );
};

export default AdminPage;
