import React from 'react';
import { Layout, Menu } from 'antd';
import { HomeOutlined, SettingOutlined, UserOutlined, AppstoreOutlined, OrderedListOutlined, MailOutlined } from '@ant-design/icons';
import { Link, Outlet } from 'react-router-dom';
import { useMenu } from './MenuContext';
import './AdminPage.css';

const { Header, Sider } = Layout;
const { SubMenu } = Menu;

const AdminPage = () => {
    const { menuData } = useMenu();

    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider>
                <div className="logo">管理系统</div>
                <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                    <Menu.Item key="1" icon={<HomeOutlined />}>
                        <Link to="/admin">首页</Link>
                    </Menu.Item>
                    {menuData.map(item => (
                        item.visible && (
                            <SubMenu key={item.key} icon={React.createElement({
                                'shop': AppstoreOutlined,
                                'ordered-list': OrderedListOutlined,
                                'mail': MailOutlined,
                                'setting': SettingOutlined,
                            }[item.icon])} title={item.name}>
                                <Menu.Item key={`${item.key}-list`}>
                                    <Link to={`/${item.key}-list`}>{item.name}列表</Link>
                                </Menu.Item>
                            </SubMenu>
                        )
                    ))}
                    <SubMenu key="permissions" icon={<SettingOutlined />} title="权限">
                        <Menu.Item key="menu-management">
                            <Link to="/admin/menu-management">菜单列表</Link>
                        </Menu.Item>
                        <Menu.Item key="user-list">
                            <Link to="/admin/user-list">用户列表</Link>
                        </Menu.Item>
                    </SubMenu>
                </Menu>
            </Sider>
            <Layout>
                <Header className="site-layout-background" style={{ padding: 0 }} />
                <Outlet />
            </Layout>
        </Layout>
    );
};

export default AdminPage;
