import React from 'react';
import { Layout, Menu } from 'antd';
import { HomeOutlined, ShopOutlined, OrderedListOutlined, MailOutlined, SettingOutlined, UserOutlined } from '@ant-design/icons';
import { Link, Outlet } from 'react-router-dom';
import './AdminPage.css';

const { Header, Sider, Content } = Layout;
const { SubMenu } = Menu;

const AdminPage = () => {
    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider>
                <div className="logo">管理系统</div>
                <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                    <Menu.Item key="1" icon={<HomeOutlined />}>
                        <Link to="/admin">首页</Link>
                    </Menu.Item>
                    <SubMenu key="product" icon={<ShopOutlined />} title="商品">
                        <Menu.Item key="product-list">商品列表</Menu.Item>
                    </SubMenu>
                    <SubMenu key="order" icon={<OrderedListOutlined />} title="订单">
                        <Menu.Item key="order-list">订单列表</Menu.Item>
                    </SubMenu>
                    <SubMenu key="marketing" icon={<MailOutlined />} title="营销">
                        <Menu.Item key="marketing-list">营销活动</Menu.Item>
                    </SubMenu>
                    <SubMenu key="permission" icon={<SettingOutlined />} title="权限">
                        <Menu.Item key="menu-management">
                            <Link to="/admin/menu-management">菜单管理</Link>
                        </Menu.Item>
                        <Menu.Item key="user-list">
                            <Link to="/admin/user-list">用户管理</Link>
                        </Menu.Item>
                    </SubMenu>
                </Menu>
            </Sider>
            <Layout>
                <Header className="site-layout-background" style={{ padding: 0 }} />
                <Content style={{ margin: '16px' }}>
                    <Outlet />
                </Content>
            </Layout>
        </Layout>
    );
};

export default AdminPage;
